document.addEventListener("alpine:init",()=>{Alpine.data("carousel",(i=0,e=!1)=>({active:0,out:null,total:i,autoplay:e,timeout:5e3,interval:null,goTo(t){this.pauseAutoplay(),this.out=this.active,t>this.total-1?this.active=0:t<0?this.active=this.total-1:this.active=t,clearInterval(this.interval),this.startAutoplay()},init(){this.startAutoplay()},startAutoplay(){if(this.autoplay){var t=this;this.interval=setInterval(function(){t.out=t.active;var a=t.active+1;a=a>t.total-1?0:a,t.active=a},t.timeout)}},pauseAutoplay(){clearInterval(this.interval)},toggleAutoplay(){this.autoplay?(this.autoplay=!1,this.pauseAutoplay()):(this.autoplay=!0,this.startAutoplay())}}))});
